import magazineSubscriberService from '@/services/magazineSubscriberService';
export default {
  props: {
    showModal: {
      type: Boolean,
      required: true
    },
    subscriber: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      internalShowModal: false,
      internalSubscriber: {},
      categories: [],
      types: []
    };
  },
  watch: {
    showModal: {
      immediate: true,
      handler(newVal) {
        this.internalShowModal = newVal;
      }
    },
    subscriber: {
      immediate: true,
      handler(newVal) {
        this.internalSubscriber = {
          ...newVal
        };
      }
    }
  },
  methods: {
    handleOk() {
      this.$emit('save', this.internalSubscriber);
      this.internalShowModal = false;
      this.$emit('update:showModal', false);
    },
    handleCancel() {
      this.internalShowModal = false;
      this.$emit('update:showModal', false);
    },
    fetchCategories() {
      magazineSubscriberService.getCategories().then(response => {
        this.categories = response.data;
      }).catch(error => {
        console.error(error);
      });
    },
    fetchTypes() {
      magazineSubscriberService.getTypes().then(response => {
        this.types = response.data;
      }).catch(error => {
        console.error(error);
      });
    }
  },
  mounted() {
    this.fetchCategories();
    this.fetchTypes();
  }
};
