import axios from 'axios';

const API_URL = 'http://localhost:8000/api/subscriptions/';
const PLANS_API_URL = 'http://localhost:8000/api/subscription-plans/';
const LANGUAGES_API_URL = 'http://localhost:8000/api/subscription-languages/';
const MODES_API_URL = 'http://localhost:8000/api/subscription-modes/';

export default {
  getSubscriptions(subscriberId) {
    return axios.get(`${API_URL}?subscriber=${subscriberId}`);
  },
  createSubscription(data) {
    return axios.post(API_URL, data);
  },
  deleteSubscription(id) {
    return axios.delete(`${API_URL}${id}/`);
  },
  getPlans() {
    return axios.get(PLANS_API_URL);
  },
  createPlan(data) {
    return axios.post(PLANS_API_URL, data);
  },
  updatePlan(id, data) {
    return axios.put(`${PLANS_API_URL}${id}/`, data);
  },
  deletePlan(id) {
    return axios.delete(`${PLANS_API_URL}${id}/`);
  },
  getLanguages() {
    return axios.get(LANGUAGES_API_URL);
  },
  getModes() {
    return axios.get(MODES_API_URL);
  }
};
