import axios from 'axios';

const apiUrl = 'http://localhost:8000/api/subscribers/';

const getSubscribers = () => {
  return axios.get(apiUrl);
};

const getSubscriberById = (id) => {
  return axios.get(`${apiUrl}${id}/`);
};

const createSubscriber = (subscriber) => {
  return axios.post(apiUrl, subscriber);
};

const updateSubscriber = (id, subscriber) => {
  return axios.put(`${apiUrl}${id}/`, subscriber);
};

const deleteSubscriber = (id) => {
  return axios.delete(`${apiUrl}${id}/`);
};

export default {
  getSubscribers,
  getSubscriberById,
  createSubscriber,
  updateSubscriber,
  deleteSubscriber
};
