export default {
  props: {
    showModal: {
      type: Boolean,
      default: false
    },
    subscriber: {
      type: Object,
      default: () => ({})
    },
    categories: {
      type: Array,
      default: () => []
    },
    types: {
      type: Array,
      default: () => []
    }
  },
  data() {
    return {
      localSubscriber: {
        ...this.subscriber
      }
    };
  },
  watch: {
    subscriber(newVal) {
      this.localSubscriber = {
        ...newVal
      };
    }
  },
  methods: {
    handleOk() {
      this.$emit('save', this.localSubscriber);
    },
    handleCancel() {
      this.$emit('cancel');
    }
  }
};
