import axios from 'axios';

const API_URL = 'http://localhost:8000/api/subscribers/';
const CATEGORIES_API_URL = 'http://localhost:8000/api/subscriber-categories/';
const TYPES_API_URL = 'http://localhost:8000/api/subscriber-types/';

export default {
  getSubscribers() {
    return axios.get(API_URL);
  },
  getSubscriber(id) {
    return axios.get(`${API_URL}${id}/`);
  },
  createSubscriber(data) {
    return axios.post(API_URL, data);
  },
  updateSubscriber(id, data) {
    return axios.put(`${API_URL}${id}/`, data);
  },
  deleteSubscriber(id) {
    return axios.delete(`${API_URL}${id}/`);
  },
  getCategories() {
    return axios.get(CATEGORIES_API_URL);
  },
  getTypes() {
    return axios.get(TYPES_API_URL);
  }
};
